import React, { useEffect, useRef, useState } from "react"
import "./App.css"
import GemSymbol from "./Gem.png"
import useInterval from "./interval"

const canvasX = 1000
const canvasY = 1000
const initialSnake = [ [ 5, 10 ], [ 5, 10 ] ]
const initialGem = [ 14, 10 ]
const scale = 50
const timeDelay = 100

function App() {
	const canvasRef = useRef<HTMLCanvasElement | null>(null)
	const [ snake, setSnake ] = useState(initialSnake)
	const [ gem, setGem ] = useState(initialGem)
	const [ direction, setDirection ] = useState([ 0, -1 ])
	const [ delay, setDelay ] = useState<number | null>(null)
	const [ gameOver, setGameOver ] = useState(false)
	const [ score, setScore ] = useState(0)

	useInterval(() => runGame(), delay)

	useEffect(
		() => {
			let Gem = document.getElementById("gem") as HTMLCanvasElement
			if (canvasRef.current) {
				const canvas = canvasRef.current
				const ctx = canvas.getContext("2d")
				if (ctx) {
					ctx.setTransform(scale, 0, 0, scale, 0, 0)
					ctx.clearRect(0, 0, window.innerWidth, window.innerHeight)
					for (let i = 0; i < 6; i++) {
						for (let j = 0; j < 6; j++) {
						  ctx.fillStyle = `rgb(
							  ${Math.floor(255 - 42.5 * i)},
							  ${Math.floor(255 - 42.5 * j)},
							  0)`;
						  ctx.fillRect(j * 25, i * 25, 25, 25);
						}
					  }
					snake.forEach(([ x, y ]) => ctx.fillRect(x, y, 1, 1))
					ctx.drawImage(Gem, gem[0], gem[1], 1, 1)
				}
			}
		},
		[ snake,gem, gameOver ]
	)

	function handleSetScore() {
		if (score > Number(localStorage.getItem("snakeScore"))) {
			localStorage.setItem("snakeScore", JSON.stringify(score))
		}
	}

	function play() {
		setSnake(initialSnake)
		setGem(initialGem)
		setDirection([ 1, 0 ])
		setDelay(timeDelay)
		setScore(0)
		setGameOver(false)
	}

	function checkCollision(head: number[]) {
		for (let i = 0; i < head.length; i++) {
			if (head[i] < 0 || head[i] * scale >= canvasX) return true
		}
		for (const s of snake) {
			if (head[0] === s[0] && head[1] === s[1]) return true
		}
		return false
	}

	function gemGet(newSnake: number[][]) {
		let coord = gem.map(() => Math.floor(Math.random() * canvasX / scale))
		if (newSnake[0][0] === gem[0] && newSnake[0][1] === gem[1]) {
			let newGem = coord
			setScore(score + 1)
			setGem(newGem)
			return true
		}
		return false
	}

	function runGame() {
		const newSnake = [ ...snake ]
		const newSnakeHead = [ newSnake[0][0] + direction[0], newSnake[0][1] + direction[1] ]
		newSnake.unshift(newSnakeHead)
		if (checkCollision(newSnakeHead)) {
			setDelay(null)
			setGameOver(true)
			handleSetScore()
		}
		if (!gemGet(newSnake)) {
			newSnake.pop()
		}
		setSnake(newSnake)
	}

	function changeDirection(e: React.KeyboardEvent<HTMLDivElement>) {
		switch (e.key) {
			case "a":
				setDirection([ -1, 0 ])
				break
			case "w":
				setDirection([ 0, -1 ])
				break
			case "d":
				setDirection([ 1, 0 ])
				break
			case "s":
				setDirection([ 0, 1 ])
				break
		}
	}

	return (
		<div onKeyDown={(e) => changeDirection(e)}>
			<img id="gem" src={GemSymbol} alt="gem" width="30" />
			<canvas className="playArea" ref={canvasRef} width={`${canvasX}px`} height={`${canvasY}px`} />
			{gameOver && <div className="gameOver">Game Over</div>}
			<button onClick={play} className="playButton">
				Play
			</button>
			<div className="scoreBox">
				<h2>Score: {score}</h2>
				<h2>High Score: {localStorage.getItem("snakeScore")}</h2>
			</div>
		</div>
	)
}

export default App
